mapper.update:
UPDATE transaction
SET payment_option=#{paymentOption}, receiver_name=#{receiverName}, receiver_phone=#{receiverPhone}, demailaddr=#{divyAddr}, dlvy_request=#{divyRequest}, dlvy_date=#{divyDate}
WHERE tran_no = #{tranNo}

UPDATE transaction
SET tran_status_code=#{tranCode}
WHERE tran_no=#{tranNo}

mapper.sql:

	 
		SELECT t.*
		FROM transaction t, product p
		WHERE t.prod_no = p.prod_no AND p.prod_name LIKE #{search.searchKeyword}
	
	  
		SELECT t.*
		FROM transaction t, product p
		WHERE t.prod_no = p.prod_no AND t.tran_no = #{search.searchKeyword}
	


	SELECT
	tran_no, prod_no, buyer_id, payment_option, receiver_name, receiver_phone, demailaddr, dlvy_request, tran_status_code, order_data, dlvy_date
	FROM transaction
	WHERE buyer_id=#{buyerId}

ORDER BY tran_no ${sort}

mapper.select:
SELECT COUNT(*) FROM (
SELECT t.* FROM transaction t, product p WHERE t.prod_no = p.prod_no AND p.prod_name LIKE #{searchKeyword}
SELECT t.* FROM transaction t, product p WHERE t.prod_no = p.prod_no AND t.tran_no = #{searchKeyword}
SELECT tran_no, prod_no, buyer_id, payment_option, receiver_name, receiver_phone, demailaddr, dlvy_request, tran_status_code, order_data, dlvy_date FROM transaction ) countTable
